package week1.day2;

public class CheckIfTheNumberIsPositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

        int num=31;
		
		if(num>0) {
			System.out.println("the number is possitive");
		}else if(num<0) {
			System.out.println("the number is negative");
		}
		else if(num==0) {
			System.out.println("The given number is neither possitive nor negative");
		}
	}

}
